function showSimpleToast(type, title, message) {
    const icons = {
        success: "✓",
        error: "!",
        warning: "!",
        info: "i"
    };

    const container = document.getElementById('toastContainer');

    const toast = document.createElement('div');
    toast.className = `simple-toast ${type}`;
    toast.innerHTML = `
            <div class="simple-toast-icon">${icons[type]}</div>
            <div class="simple-toast-content">
                <div class="simple-toast-title">${title}</div>
                <div class="simple-toast-message">${message}</div>
            </div>
            <button class="simple-toast-close" onclick="closeSimpleToast(this)">×</button>
        `;

    container.appendChild(toast);

    setTimeout(() => {
        closeSimpleToast(toast.querySelector('.simple-toast-close'));
    }, 6000);
}

function closeSimpleToast(btn) {
    const toast = btn.closest('.simple-toast');
    toast.classList.add('removing');
    setTimeout(() => {
        toast.remove();
    }, 300);
}